import Link from "next/link";
import Image from "next/image";

export interface ResultProps {
  backdrop_path?: string;
  id: number;
  title: string;
  original_title: string;
  overview: string;
  poster_path?: string;
  media_type: string;
  adult: boolean;
  original_language: string;
  genre_ids: number[];
  popularity: number;
  release_date: string;
  video: boolean;
  vote_average: number;
  vote_count: number;
}

const Results = ({ result }: { result: ResultProps }) => {
  const url = "https://image.tmdb.org/t/p/original";
  return (
    <div className="group">
      <Link href={`/movie/${result.id}`}>
        <Image
          src={`${url}${result.backdrop_path || result.poster_path}`}
          alt={`Official movie poster for ${result.title}`}
          width={500}
          height={300}
          className="sm:rounded-t-lg group-hover:opacity-50 transition-opacity duration-300"
        />
      </Link>
    </div>
  );
};

export default Results;
